import Foundation

func isSubsequence(_ s: String, _ t: String) -> Bool {
    if s.isEmpty {return true}
    
    var index = s.startIndex
    
    for char in t {
        if char == s[index] {
            index = s.index(after: index)
            if index == s.endIndex {return true}
        }
    }
    
        
    return false
}

isSubsequence("leeeeetcode", "")

